dict.string:
import json
import os

# Path to the gridItems.json file inside the workflow folder
# workflow_folder = os.getenv("alfred_workflow_data")
json_file = "./items.json"

# Load JSON data
try:
    with open(json_file, "r") as f:
        items = json.load(f)
except Exception as e:
    print(json.dumps({
        "items": [
            {"title": "Error", "subtitle": str(e), "valid": False}
        ]
    }))
    exit()

# Build Alfred results
results = {"items": []}
for item in items:
    results["items"].append({
        "title": item["title"],
        "arg": item["selection"],  # Pass the "selection" property on selection
        "icon": {"path": item["image"]},  # Use the image path for grid view
    })

# Output results in Alfred-compatible JSON
print(json.dumps(results))

